INSERT INTO `ym_classes` (`id`, `title`, `summary`, `capacity`, `price`, `startSignupDate`, `endSignupDate`, `startClassDate`, `endClassDate`, `category_id`, `course_id`, `teacher_id`, `order`, `sessions`, `startClassTime`, `endClassTime`, `classDays`, `status`) VALUES
(1, 'اسپیکینگ تافل آی بی تی گروه 1', '<p dir="RTL">دردوره اسپیکینگ تافل آی بی تی، علاوه بر معرفی هر 6 صورت سوال این بخش و تکنیک های مورد نیاز، تلاش خواهد شد که دانش زبانی داوطلبین هم بهتر شود. در ضمن <u><strong>مهارت نت برداری</strong></u> که یکی از اصلی ترین مهارت های تافل آی بی تی است نیز با دقت و ظرافت تدریس خواهد شد.&nbsp;با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم.</p>\r\n\r\n<p dir="RTL">با آرزوی بهروزی همه عزیزان</p>\r\n\r\n<p dir="RTL">مدرس تافل موسسه پردیس آوای شهیر</p>\r\n\r\n<p><span dir="RTL">امیر خادم المله</span></p>\r\n', 20, 250000, '1466191800', '1470079800', '1470166200', '1473795000', 21, 13, 33, 1, '6', '1', '4', 'چهارشنبه', 1),
(2, 'رایتینگ تافل آی بی تی گروه 1', '<p dir="RTL" style="text-align:justify">دوره های آموزش تافل که قرار است در موسسه پردیس آوای شهیر برگزار شود با دوره هایی که در دانشگاه شریف برگزار میکنم تفاوت دارد. در این دوره ها، علاوه بر نمونه هایی که در طول دوره تصحیح میگردند،&nbsp;دو نمونه از رایتینگ های هر داوطلب به طور رایگان در طول ترم تصحیح میگردد که تاثیر به سزایی در نمره تافل آنها خواهد داشت.&nbsp;در انتهای هر دوره نیز، بنا به نیاز و درخواست&nbsp;زبان آموزان، یک گارکاه چند ساعته برگزار خواهد شد که در آن صرفا نمونه رایتینگ هر یک از داوطلبین برسی شده ونکات کلیدی و نهایی به آنها آموخته میشود. با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم.</p>\r\n\r\n<p dir="RTL">با آرزوی بهروزی همه عزیزان</p>\r\n\r\n<p dir="RTL">مدرس تافل موسسه پردیس آوای شهیر&nbsp;</p>\r\n\r\n<p><span dir="RTL">امیر خادم المله</span></p>\r\n', 25, 400000, '1466191800', '1468351800', '1468438200', '1473881400', 22, 13, 33, 3, '10', '1', '4', 'پنجشنبه', 0),
(3, 'رایتینگ تافل آی بی تی گروه 2', '<p dir="RTL" style="text-align:justify">دوره های آموزش تافل که قرار است در موسسه &nbsp;پردیس آوای شهیر برگزار شود با دوره هایی که در دانشگاه شریف برگزار میکنم تفاوت دارد. در این دوره ها، علاوه بر نمونه هایی که در طول دوره تصحیح میگردند،&nbsp;دو نمونه از رایتینگ های هر داوطلب به طور رایگان در طول ترم تصحیح میگردد که تاثیر به سزایی در نمره تافل آنها خواهد داشت.&nbsp;در انتهای هر دوره نیز، بنا به نیاز و درخواست زبان آموزان، یک گارکاه چند ساعته برگزار خواهد شد که در آن صرفا نمونه رایتینگ هر یک از داوطلبین برسی شده ونکات کلیدی و نهایی به آنها آموخته میشود. با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم.</p>\r\n\r\n<p dir="RTL">با آرزوی بهروزی همه عزیزان</p>\r\n\r\n<p dir="RTL">مدرس تافل موسسه پردیس آوای شهیر</p>\r\n\r\n<p><span dir="RTL">امیر خادم المله</span></p>\r\n', 25, 400000, '1466191800', '1470166200', '1470252600', '1475699400', 22, 13, 33, 4, '10', '5', '8', 'پنجشنبه', 1),
