INSERT INTO `ym_courses` (`id`, `title`, `pic`, `summary`, `order`, `seen`) VALUES
(13, 'تافل آی بی تی', 'Wkt8u1463598016.png', '<p dir="RTL">دوره های تافل آی بی تی که زیر نظر اینجانب، امیر خادم، در موسسه آوای شهیر برگزار می شود رویکردی متفاوت از مراکز دیگر خواهد داشت.</p>\r\n\r\n<p dir="RTL">مدیریت موسسه آوای شهیر: امیر خادم</p>\r\n', 1, 1641),
(15, 'آیلتس ', 'VtRG01462256706.jpg', '<p>دوره های آموزش آیلتس موسسه پردیس آوای شهیر نیز مانند دوره های تافل آن تفاوت های چشمگیری دارد با دوره هایی که خود اینجانب، امیر خادم، سابقا برگزار میکردم و دوره هایی که موسسات دیگر برگزار میکنند. اول اینکه در این دوره ها تخفیف های ویژه ای برای دانشجویان عزیز در نظر گرفته شده &ndash; که بخشی از آن در سایت نت برگ ارائه خواهد شد &ndash; که شرایط ثبت نام را برای آنها ایده آل میکند. دوم اینکه، برعکس تمام موسسات، دو نمونه از رایتینگ های هر داوطلب به طور رایگان در طول ترم تصحیح میگردد که تاثیر به سزایی در نمره آیلتس آنها خواهد داشت. سوم اینکه در انتهای هر دوره، بنا به نیاز زبان آموزان، یک گارکاه چند ساعته برگزار خواهد شد که در آن صرفا نمونه رایتینگ هر یک از داوطلبین برسی شده ونکات کلیدی و نهایی به آنها آموخته میشود. با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم. با آرزوی بهروزی همه عزیزان مدیر موسسه پردیس آوای شهیر: امیر خادم المله</p>\r\n', 2, 424),
(16, 'جی آر ای', 'evVNz1462629946.png', '<p>کتاب ها و منابعی که برای امتحان جی آر ای ارائه شده اند بسیاراند، ولی با خواندن این منابع گلچین شده میتوانید نمره دلخواهتان را بگیرید.</p>\r\n\r\n<p>با آرزوی موفقیت شما</p>\r\n\r\n<p>امیر خادم: مدیر موسسه پردیس آوای شهیر</p>\r\n', 5, 80),
(17, 'جی ام ای تی (جی مت)', 'rQ4U41462692555.png', '<p>این منابع از بهترین منابع امتحان&nbsp;جی ام ای تی (جی مت) هستند که امیدواریم برایتان سودمند باشند.&nbsp;</p>\r\n\r\n<p>امیر خادم</p>\r\n', 6, 58),
(18, 'تافل دکتری', '8971n1462633879.png', '<p>امتحان تافل دکتری، که برای دانشجویان دکتری در ایران برگزار میگردد،&nbsp;همان امتحان تافل پی بی تی است با این تفاوت که بخش رایتینگ ندارد. بنابراین، خواندن این منابع برای یک نمره خوب کافی است.</p>\r\n', 4, 57),
(20, 'دوره گرامر Pre-TOEFL & Pre-IELTS ', 'tByGL1462625982.jpg', '<p>این دوره برای عزیزانی تعریف شده که سطح زبانی آنها پایین تر از سطح متوسط می باشد و آنها را برای کلاس های تافل آی بی تی، آیلتس جنرال و آکادمیک، جی آر ای، جی ام ای تی، و تافل دکتری آماده می سازد.&nbsp;</p>\r\n', 3, 232),
(21, 'کارگاه ها', 'fsDA91464366763.jpg', '<p>موسسه آوای شهیر افتخار دارد که&nbsp;هر چند وقت یکبار کارگاه های متنوعی در خصوص آزمون های تافل، آیلتس، جی آر ای، و جی مت و همچنین کارگاه های حرفه ای مقاله نویسی برگزار نماید.</p>\r\n', 7, 84);
