(3, 'تماس با ما', '<p>sadf</p>\r\n', 4),
(5, 'قوانین و مقررات', '<p dir="RTL"><strong>قوانین و مقررات موسسه آوای شهیر</strong></p>\r\n\r\n<ol>\r\n	<li dir="RTL">رعایت شئونات اسلامی،&nbsp;قوانین، و مقررات جمهوری اسلامی ایران و همچنین مقررات موسسه الزامی میباشد<span dir="LTR">.</span></li>\r\n	<li dir="RTL">رعایت ادب در فضای مجازی چه در سایت چه در تلگرام الزامی می باشد<span dir="LTR">.</span></li>\r\n	<li dir="RTL">هیچ یک از اساتید، کارمندان، و مراجعین حق ندارند مسائل سیاسی را در محیط آموزشگاه و یا در فضای سایبری موسسه مطرح کند.</li>\r\n	<li dir="RTL">شهریه به هیچ عنوان مسترد نمی گردد<span dir="LTR">.</span></li>\r\n	<li dir="RTL">همراه داشتن فیش ثبت نام برای اولین حضور سر کلاس&nbsp;الزامی می باشد<span dir="LTR">.</span></li>\r\n	<li dir="RTL">تغییر روز و ساعت کلاس امکان پذیر نمی باشد مگر با موافقت کتبی کلیه دانشجویان و استاد و تایید موسسه<span dir="LTR">.</span></li>\r\n	<li dir="RTL">استفاده از تلفن همراه در طول مدت کلاس ممنوع می باشد<span dir="LTR">.</span></li>\r\n	<li dir="RTL">توضیحات مندرجه در مورد هر دوره را با دقت مطالعه نموده و متناسب با این توضیحات ثبت نام فرمایید<span dir="LTR">.</span></li>\r\n	<li dir="RTL">اطلاعیه ها و دستورالعمل هایی که&nbsp;در سایت و یا از طریق تلگرام در اختیارتان قرار میگیرد&nbsp;را بدقت مطالعه فرمایید<span dir="LTR">.</span></li>\r\n	<li dir="RTL">آوای شهیر هرگز اطلاعات شما را بصورت تلفنی یا از طریق پیامک درخواست نخواهد کرد. درصورتیکه فردی با این عنوان اطلاعات شما را درخواست نمود، موارد را به مدیریت گزارش فرمایید<span dir="LTR">.</span></li>\r\n	<li dir="RTL"><span dir="RTL">هرگونه مورد نامناسبی</span>&nbsp;<span dir="RTL">در فضای کلاس و موسسه را بلافاصله به مدیریت گزارش فرمایید</span>.</li>\r\n</ol>\r\n', 1),
(12, 'درباره ما', '<p dir="RTL" style="text-align:justify">تلفیق تحصیلات آکادمیک و سالها تجربه تدریس و ترجمه در سطوح مختلف از سال 1381 تا کنون در موسسات معتبر و&nbsp;مراکز علمی&nbsp;و&nbsp;دانشگاهی از قبیل جهاد دانشگاهی علامه طباطبایی، دانشگاه علمی کاربردی، و دانشگاه شریف منجر به&nbsp;پایه گذاری موسسه علمی-فرهنگی پردیس آوای شهیر در سال 1394 شد&nbsp;که در زمینه های فرهنگی وهنری به طور عام و آموزش زبان انگلیسی به طور خاص فعالیت خود را آغاز کرده و تلاش میکند تا خدماتی نو و خلاقانه را&nbsp;به&nbsp;مردم عزیز ایران ارائه&nbsp;کند. امیدواریم سابقه چندین ساله&nbsp;تحقیق و تدریس اینجانب و اساتید مجرب موسسه آوای شهیر&nbsp;در زمینه زبان انگلیسی راه گشای عزیزانی باشد که قصد همراه شدن با ما را دارند.</p>\r\n\r\n<p dir="RTL" style="text-align:justify">به امید بهروزی شما</p>\r\n\r\n<p dir="RTL" style="text-align:justify">مدیر موسسه آوای شهیر:&nbsp;امیر خادم المله</p>\r\n', 4),
(14, 'راهنما', '<p>ستیدبمشسکدئیب</p>\r\n', 2);
