INSERT INTO `ym_articles` VALUES ('15', 'نرم افزارهای آموزشی و کاربردی Applications & Educational Software', '<p>این نرم افزارها برای استفاده کاربران عزیز در اینجا گذاشته شده که در استفاده از منابع آموزشی به آنها کمک زیادی خواهد کرد.</p>\n', 'HeSTS1485913450.jpg', '1480709113', '1480709113', '198', 'publish', '5', '1');
INSERT INTO `ym_articles` VALUES ('17', 'آدرس سایت های دیگر', '<p>در اینجا میتوانید آدرس سایت های دیگری که مطالب آموزنده و یا منابع کاربردی مفیدی دارند را مشاهده کنید. </p>\n', 'V6JSA1480854903.jpg', '1480713899', '1480713899', '182', 'publish', '4', '2');
INSERT INTO `ym_articles` VALUES ('18', 'دانلود کتاب های لغت و گرامر Vocabulary and Grammar', '<p>در این بخش می توانید کتاب های گرامر و لغت مفید را دانلود کنید.</p>\n', 'wIkJe1485913524.jpg', '1480845160', '1480845490', '739', 'publish', '3', '3');
INSERT INTO `ym_articles` VALUES ('19', 'دانلود ویدیو های آموزشی گرامر', '<p>در این بخش می توانید ویدیو های آموزش گرامر را دانلود کنید.</p>\n', 'wzLHt1485913374.jpg', '1481021631', '1481021631', '86', 'publish', '6', '4');
INSERT INTO `ym_articles` VALUES ('20', 'دانلود کتاب های رایتینگ  Useful Writing Books', '<p>در این بخش می توانید کتاب های مفید برای تقویت رایتنگ در زمینه های مختلف را دانلود کنید.</p>\n', 'ovu7L1485913776.jpg', '1481498312', '1481498312', '243', 'publish', '3', '5');
INSERT INTO `ym_articles` VALUES ('21', 'فایل های آموزش مهارت لیسنینگ  Listening Skills', '<p>این فایل ها برای تقویت زبان عمومی و مهارت لیسنینگ با لهجه بریتانیایی، آمریکایی، واسترالیایی فوق العاده هستند.</p>\n', 'R1iTs1485912854.jpg', '1481802883', '1481802883', '56', 'draft', '7', '6');
INSERT INTO `ym_articles` VALUES ('23', 'فایل های صوتی انگیزشی', '<p>این فایل ها هم مهارت لیسنینگ شما رو تقویت میکنند و هم فایل های انگیزشی بسیاری خوبی هستند که در زندگی روزمره هم تاثیر فوق العاده ای دارند. این فایل ها برای زبان آموزان سطح متوسط به بالا مناسب هستند.</p>\n', 'NfDny1482006176.jpg', '1482006469', '1482006469', '29', 'draft', '9', '7');
INSERT INTO `ym_articles` VALUES ('25', 'فایل های صوتی با لهجه آمریکایی American Accent', '<p>این فایل های صوتی برای یادگیری لهجه آمریکایی بسیار مناسب هستند و برای تمامی سطوح مطالب متنوع دارند.</p>\n', 'GSHGF1482012094.jpg', '1482011281', '1482011281', '97', 'publish', '7', '8');
INSERT INTO `ym_articles` VALUES ('26', 'فایل های صوتی با لهجه بریتانیایی British Accent', '<p>این فایل های صوتی برای یادگیری لهجه بریتانیایی بسیار مناسب هستند و برای تمامی سطوح مطالب متنوع دارند.</p>\n', 'mdfvu1482013374.jpg', '1482013676', '1482013676', '92', 'publish', '7', '9');
INSERT INTO `ym_articles` VALUES ('28', 'برایان تریسی Brian Tracy', '<p>فایل های صوتی برایان تریسی هم مهارت لیسنینگ شما رو تقویت میکنند و هم فایل های انگیزشی بسیار خوبی هستند که در زندگی روزمره هم تاثیر فوق العاده ای دارند. این فایل ها برای زبان آموزان سطح متوسط به بالا مناسب هستند. </p>\n', 'yxlgd1482018641.jpg', '1482018892', '1482018892', '88', 'publish', '9', '10');
INSERT INTO `ym_articles` VALUES ('29', 'کتاب فوق العاده Think and Grow Rich ', '<p>این کتاب یکی از بهترین کتاب های انگیزشی دنیا به حساب می آید و برای سطح پیشرفته مناسب است.</p>\n', 'KvBh11482579128.jpg', '1482579908', '1482579908', '49', 'publish', '9', '11');
INSERT INTO `ym_articles` VALUES ('30', 'انگلیسی تجاری و بازرگانی Business English', '<p>در این مجموعه، فایل های متنی، صوتی،و فیلم های آموزشی انگلیسی تجاری و بازرگانی را رایگان می توانید دانلود کنید.</p>\n', 'AZJMN1483287491.jpg', '1483289375', '1483289375', '37', 'publish', '15', '12');
INSERT INTO `ym_articles` VALUES ('31', 'معرفی نامه و اس او پی برای رشته های مختلف', '<p>این نمونه ها صرفا برای آشنایی شما با فرمت و ساختار اس او پی ارائه شده. بنابر این، باید از کپی کردن جمله و حتی عبارت های طولانی جدا خودداری کنید که متهم به سرقت ادبی نشوید.</p>\n', 'epNlS1483551575.jpg', '1483543187', '1483543187', '408', 'publish', '16', '13');
