INSERT INTO `ym_classes` VALUES ('1', 'اسپیکینگ تافل آی بی تی گروه 1', '<p>دردوره اسپیکینگ تافل آی بی تی، علاوه بر معرفی هر 6 صورت سوال این بخش و تکنیک های مورد نیاز، تلاش خواهد شد که دانش زبانی داوطلبین هم بهتر شود. در ضمن <u><strong>مهارت نت برداری</strong></u> که یکی از اصلی ترین مهارت های تافل آی بی تی است نیز با دقت و ظرافت تدریس خواهد شد. با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم.</p>\r\n\r\n<p>با آرزوی بهروزی همه عزیزان</p>\r\n\r\n<p>مدرس تافل موسسه پردیس آوای شهیر</p>\r\n\r\n<p>امیر خادم المله</p>\r\n', '20', '300000', '1484857800', '1493753400', '1493753400', '1496777400', '21', '13', '33', '1', '6', '5', '8', 'چهارشنبه', '1', '0');
INSERT INTO `ym_classes` VALUES ('2', 'رایتینگ تافل آی بی تی گروه 1', '<p style=\"text-align:justify;\">دوره های آموزش تافل که قرار است در موسسه پردیس آوای شهیر برگزار شود با دوره هایی که در دانشگاه شریف برگزار میکنم تفاوت دارد. در این دوره ها، علاوه بر نمونه هایی که در طول دوره تصحیح میگردند، یک نمونه از رایتینگ های هر داوطلب به طور رایگان در طول ترم تصحیح میگردد که تاثیر به سزایی در نمره تافل آنها خواهد داشت. در انتهای هر دوره نیز، بنا به نیاز و درخواست زبان آموزان، یک گارکاه چند ساعته برگزار خواهد شد که در آن صرفا نمونه رایتینگ هر یک از داوطلبین برسی شده ونکات کلیدی و نهایی به آنها آموخته میشود. با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم.</p>\n\n<p>با آرزوی بهروزی همه عزیزان</p>\n\n<p>مدرس تافل موسسه پردیس آوای شهیر </p>\n\n<p>امیر خادم المله</p>\n', '25', '450000', '1484771400', '1492630200', '1492630200', '1498073400', '22', '13', '33', '3', '10', '1.30', '4.30', 'پنجشنبه', '1', '0');
