INSERT INTO `ym_classes` VALUES ('7', 'ریدینگ آیلتس آکادمیک: گروه 1', '<p>دوره آماده سازی ریدینگ آزمون آیلتس آوای شهیر، همچون دیگر دوره های این مرکز، از ویژگی های خاصی برخوردار است. این مرکز در تلاش است تا با بکارگیری تکنیک های موثر و برنامه ریزی فشرده، زبان پژوهان را در کوتاه ترین زمان ممکن به نتیجه دلخواه برساند. ویژگی بارز این دوره آموزش تکنیک ها به روش عملی و گام به گام می باشد. شرکت در دو آزمون ریدینگ شبیه سازی شده در آغاز و پایان دوره، هدیه ویژه آوای شهیر به زبان پژوهان خود می باشد.<br />\r\nاهداف دوره:&nbsp;<br />\r\n۱. آشنایی با انواع متون آزمون آیلتس<br />\r\n۲. آشنایی کامل با انواع سوالات آزمون ریدینگ<br />\r\n۳. تسلط بر تکنیک های مناسب برای پاسخ گویی به انواع سوالات<br />\r\n۴. آشنایی با روش های تند خوانی و نحوه بررسی متون<br />\r\n۵. افزایش سرعت پاسخ گویی به سوالات<br />\r\n۶. بهبود عملکرد داوطلبان، به میزان ۱ تا ۲.۵ نمره<br />\r\nپردیس آوای شهیر امیدوار است تا با ارائه دوره های موثر و کوتاه مدت، سریع ترین و نتیجه بخش ترین مسیر ممکن را در اختیار همراهان خود قرار دهد.<br />\r\nوحید ایزدی فر<br />\r\nمدرس آیلتس آموزشگاه پردیس آوای شهیر</p>\r\n', '10', '250000', '1475094600', '1475699400', '1475699400', '1478723400', '29', '15', '73', '6', '6', '5', '8', 'پنجشنبه', '1', '0');
INSERT INTO `ym_classes` VALUES ('8', 'رایتینگ آیلتس آکادمیک: گروه 1', '<p>موسسه پردیس آوای شهیر در تلاش است تا با برنامه ریزی دوره های فشرده و کوتاه مدت، میانبرهایی کار آمد و ثمر بخش را در اختیار داوطلبان عزیز قرار دهد. از ویژگی های بارز این دوره ،آشنایی بخش به بخش با تمام فرمت های رایتینگ و آموختن تمام نکات نمره آور و مهم آزمون آیلتس می باشد. از دیگر ویژگی های مهم این دوره، بررسی تعداد زیادی از رایتینگ های موفق داوطلبان پیشین و همچنین تصحیح رایتینگ داوطلبان در طول دوره می باشد. لازم به ذکر است که تصحیح دو نمونه رایتینگ هر داوطلب هدیه ویژه آوای شهیر به دانش آموزان خود می باشد.<br />\nپردیس آوای شهیر امیدوار است با ارائه دوره های متنوع و موثر، زبان پژوهان خود را در رسیدن به اهدافشان یاری کند.<br />\nوحید ایزدی فر<br />\nمدرس آیلتس آموزشگاه پردیس آوای شهیر</p>\n', '25', '400000', '1485635400', '1487190600', '1487190600', '1493839800', '29', '15', '73', '7', '10', '4', '7', 'پنجشنبه', '1', '0');
