INSERT INTO `ym_classes` VALUES ('18', ' گرامر تافل، آیلتس، جی آر ای، و جی مت سطح متوسطه', '<p>دوره گرامر مقدماتی برای آزمون های تافل، آیلتس، جی آر ای، جی مت، و تافل دکتری<br />\nدر کلاس های گرامر مقدماتی برگزار انواع جملات، زمانها، جملات معلوم و مجهول، صفت، قید، اسم، فعل، ضمایر، و افعال کمکی تدریس خواهد شد. لازم به ذکر است که بخشی از این مطالب از \"جزوه گرامر مقدماتیِ\" اینجانب، امیر خادم، و بخش هایی هم از جزوه استاد ایزدیفر تدریس می گردد.</p>\n', '25', '400000', '1480192200', '1484598600', '1483389000', '1488832200', '35', '20', '73', '16', '10', '5', '8', 'سه شنبه', '1', '0');
