INSERT INTO `ym_class_categories` VALUES ('24', 'در مورد دوره تافل موسسه آوای شهیر ', '13', '<p dir=\"rtl\" style=\"text-align:justify;\">دوره های آموزش تافل که قرار است در موسسه پردیس آوای شهیر برگزار شود با دوره هایی که در دانشگاه شریف برگزار میکنم تفاوت هایی دارد که قابل تامل اند. اول اینکه، در این دوره ها، تخفیف های ویژه ای برای دانشجویان عزیز در نظر گرفته شده – که بخشی از آن در سایت نت برگ ارائه خواهد شد – که  شرایط ثبت نام را برای آنها ایده آل میکند. دوم اینکه، برعکس اکثر موسسات، یک نمونه از رایتینگ های هر داوطلب به طور رایگان در طول ترم تصحیح میگردد که تاثیر به سزایی در نمره تافل آنها خواهد داشت. سوم اینکه در انتهای هر دوره، بنا به نیاز زبان آموزان، یک گارکاه چند ساعته برگزار خواهد شد که در آن صرفا نمونه رایتینگ هر یک از داوطلبین بررسی شده ونکات کلیدی و نهایی به آنها آموخته میشود. با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم.</p>\n\n<p dir=\"rtl\" style=\"text-align:justify;\">با آرزوی بهروزی همه عزیزان</p>\n\n<p dir=\"rtl\" style=\"text-align:justify;\">مدیریت موسسه پردیس آوای شهیر</p>\n\n<p style=\"text-align:justify;\"><span dir=\"rtl\">امیر خادم المله</span></p>\n', '1');
