INSERT INTO `ym_comments` VALUES ('Pages', '2', '230', null, '118', null, null, 'استاد ببخشید میشه بگید punctuation اولی چرا غلطه؟ خوب یه توضیح اضافه رو بین دو تا دش آوردیم دیگه. با همه چیزهایی که درس دادید جور در میاد. \r\nو دیگه این که توی آخری که گفتید درسته، مگه قرار نبود بین subject و predicate کاما نگذاریم مگر در حالتی که nonrestrictive adj clause داشته باشیم، پس جرا اینجا قبل از are کاما باید بگذاریم؟ :(\r\nممنون میشم از این گیج شدگی درم بیارید :)\r\nمرسی.', '1470745699', null, '0');
