INSERT INTO `ym_faq` VALUES ('1', '1', 'ریدینگ آیلتس (جنرال و آکادمیک)', '<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آزمون ریدینگ چند بخش دارد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">سه بخش </span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آیا آزمون ریدینگ جنرال و آکادمیک با هم فرق دارند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">بله، آزمون آکادمیک سه بخش دارد و در هر بخش یک متن طولانی و 12-13 سوال به شما داده می شود. آزمون جنرال هم سه&nbsp; بخش دارد با این تفاوت که در هر بخش چند متن کوتاه ارائه می شوند.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آیا متن آزمون آکادمیک از جنرال سخت تر است؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">بله</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آزمون ریدینگ چند سوال دارد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">چهل سوال</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">در آزمون ریدینگ چند دقیقه زمان داریم؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">شصت دقیقه</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آیا پس از اتمام این شصت دقیقه برای انتقال پاسخ ها به پاسخ نامه زمان داده می شود؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">خیر، باید در همان شصت دقیقه پاسخ های خود را به پاسخ نامه انتقال دهید.</span></p>\r\n', '4');
INSERT INTO `ym_faq` VALUES ('2', '1', 'عمومی', '<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیلتس تحت نظارت کدام سازمان ها برگزار می شود؟</span></strong></span></p>\r\n\r\n<p>CAMBRIDGE ENGLISH LANGUAGE ASSESSMENT, idp IELTS AUSTRALIA, BRITISH COUNCIL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیلتس در کدام کشور ها معتبر است؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">تمام کشور های دنیا از جمله آمریکا</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیا تمام دانشگاه ها و موسسات علمی آیلتس را می پذیرند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">خیر، برخی موسسات و دانشگاه ها به ویژه تعداد محدودی از دانشگاه های امریکایی تافل را ترجیح می دهند.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیا موسسات برگزار کننده آیلتس در ایران اعتبار بین المللی دارند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">بله، تمام موسسات ذکر شده در سایت سازمان سنجش از اعتبار بین المللی برخوردار هستند و از نظر اعتباری هیچ تفاوتی با مراکز دیگر کشور ها ندارند.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آزمون آیلتس چند بخش دارد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">آزمون آیلتس در چهار بخش اسپیکینگ، لیسنینگ، ریدینگ، و رایتینگ برگزار می شود.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آزمون آیلتس چند نوع دارد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">آزمون آیلتس در دو گروه آکادمیک و جنرال برگزار می شود.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">تفاوت دو گروه آکادمیک و جنرال در چیست؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">آزمون آکادمیک با اهدف تحصیلی و آموزشی و آزمون جنرال با اهدف کاری و مهاجرتی سطح زبانی داوطلبان را می سنجند.</span></p>\r\n', '1');
INSERT INTO `ym_faq` VALUES ('3', '1', 'لیسنینگ آیلتس (آکادمیک و جنرال)', '<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آزمون لیسنینگ چند بخش دارد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">چهار بخش که در هر بخش باید به 10 سوال پاسخ دهید.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیا آزمون لیسنینگ جنرال و آکادمیک با هم فرق دارند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">خیر</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آزمون لیسنینگ چند دقیقه طول می کشد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">30 دقیقه</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیا پس از اتمام آزمون لیسنینگ برای انتقال پاسخ ها به پاسخ نامه زمان دارم؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">بله، پس از اتمام آزمون 10 دقیقه زمان دارید تا پاسخ های خود را به پاسخ نامه انتقال دهید.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیا در آزمون لیسنینگ هر بخش سخت تر از بخش قبلی است؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">بله</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000FF\"><strong><span dir=\"RTL\">آیا در آزمون لیسنینگ فقط لهجه بریتیش شنیده می شود؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">خیر، لهجه های دیگر همچون امریکایی، استرالیایی، نیوزلندی و .... هم شنیده می شوند.</span></p>\r\n', '3');
INSERT INTO `ym_faq` VALUES ('4', '1', 'اسپیکینگ آیلتس (آکادمیک و جنرال)', '<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آزمون اسپیکینگ چند بخش دارد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">آزمون اسپیکینگ در سه بخش برگزار می شود. </span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">در بخش اول آزمون اسپیکینگ چه سوالاتی پرسیده می شوند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">در بخش اول ممتحن سوالاتی در مورد خود داوطلب از او می پرسد. ( نام، تحصیلات، تجربیات، علایق و ....)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">در بخش دوم آزمون اسپیکینگ چه سوالاتی پرسیده می شوند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">در این بخش یک برگه سوال به داوطلب داده می شود که در آن یک موضوع با چند سوال مرتبط وجود دارد. علاوه بر این یک برگه سفید به همراه یک مداد هم داده می شود که داوطلب می تواند در مدت یک دقیقه پلن اسپیکینگ خود را بر روی آن طراحی کند.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آیا این برگه بر نمره ما تاثیر می گذارد و حتما باید چیزی بر روی آن بنویسیم؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">به هیچ عنوان، شما حتی می توانید به زبان فارسی بر روی آن بنویسید و مطالبی که بر روی آن می نویسید هیچ تاثیری بر روی نمره اسپیکینگ شما ندارد.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">در بخش دوم، چه مدت زمان باید صحبت کنم؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">بین یک تا دو دقیقه، در صورت نیاز ممتحن صحبت شما را قطع می کند و این به این معنا نیست که شما خوب و یا بد صحبت کرده اید.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">در بخش سوم آزمون اسپیکینگ چه سوالاتی پرسیده می شوند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">در این بخش سوالاتی انتزاعی تر و معمولا مرتبط با موضوع بخش دوم پرسیده می شوند.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آزمون اسپیکینگ آیلتس چند دقیقه است؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">&nbsp;یازده تا چهارده دقیقه.</span></p>\r\n', '2');
INSERT INTO `ym_faq` VALUES ('5', '1', 'رایتینگ آیلتس (آکادمیک و جنرال)', '<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آزمون رایتینگ چند بخش دارد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">دو بخش، تسک یک و تسک دو.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آیا رایتینگ آکادمیک با جنرال فرق دارند؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">بله،&nbsp; تسک یک آکادمیک گزارش نویسی و تسک یک جنرال نامه نویسی می باشد.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">در آزمون رایتینگ باید چند واژه بنویسم؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">برای تسک یک حداقل 150 واژه و برای تسک دو حداقل 250 واژه.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آزمون رایتینگ چند دقیقه طول میکشد؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">جمعا یک ساعت که چهل دقیقه آن مختص تسک دو و بیست دقیقه مختص تسک یک می باشد.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آیا میتوانم ابتدا تسک دو و سپس تسک یک را بنویسیم؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">خیر، ابتدا برگه های مربوط به تسک یک به شما داده می شوند و پس از بیست دقیقه تاپیک تسک دو به شما داده می شود.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000CD\"><strong><span dir=\"RTL\">آیا میتوانم رایتینگ خود را با خودکار بنویسم؟</span></strong></span></p>\r\n\r\n<p><span dir=\"RTL\">خیر، استفاده از خود کار و اتود در هر سه بخش ممنوع می باشد و تنها می توان از مداد استفاده کرد.</span></p>\r\n', '5');
