INSERT INTO `ym_news` VALUES ('32', 'نمره تافل آی بی تی آقای علی طلعت سال 2014', 'سلام آقای خادم. اول از همه، بابت تاخیر تو ارسال کارنامم عذر می خوام. \nدرس خوندن با شما تجربه ی جالبی بود که همیشه تو خاطرم خواهد موند. بادم نمیره که جلسه اول نمی تونستم بنویسم (یک مدیر لایق نمیتواند شرکتش را به خوبی اداره کند) ولی آخر تو رایتینگ مجموعا (380+581)961 لغت نوشتم که شد ماکس نمره هام. 26 \nمعنی sack یا rude رو نمیدونستم ولی reading م شد 23\nتلفظ  کلمه ای مثلncrease  رو نزدیک 10 بار بهم گفتینs  نه z، تا اینکهspeaking  23 شدم\nتنها listening  اذیتم کرد که دوتا دلیل داشت اونم یکی تاخیر 4 ساعته امتحانم و بی اطلاع بودن از فایل تکراری ها بود. (یه ذره بد شانسی آوردم)\nکلا راضیم چون من 5-6 ماه قبل تافل زبان کنکور ارشد رو منفی (یک ممیز سی و سه) زدم ولی تو چهار ماه این نمره رو گرفتم. حتی نزدیکتزین دوستامم باور نمی کردند من بالای 70 شم، ولی شدم و اینه که برام ارزشه.\nبراتون بهترینا رو آرزو می کنم.\n', '<p dir=\"rtl\">نمره یکی از دانشجویان تافل آی بی تی من، آقای علی طلعت، در سال 2014.</p>\n', 'YXQxL1477818136.jpg', '91', '1477818269', '1477818269', 'publish', '13', null);
