INSERT INTO `ym_news` VALUES ('39', 'نمره  جی آر ای  خانم ستاره سراچی سال 2014', '', '<p>نمره یکی از دانشجویان جی آر ای من، خانم ستاره سراچی، در سال 2014.</p>\n\n<p dir=\"ltr\"><span dir=\"ltr\">Dear Mr. Khadem, </span></p>\n\n<p dir=\"ltr\"><span dir=\"ltr\">This morning, I received my GRE scores, thus this chapter of my life is now closed. No more exams for now :) </span></p>\n\n<p dir=\"ltr\"><span dir=\"ltr\">I wish I’d had a chance to say a proper thank you in person to you for being such a fantastic teacher. I had a terrific experience in your class. Your extensive knowledge and enthusiasm made the class a pleasure to study for.</span></p>\n\n<p dir=\"ltr\"><span dir=\"ltr\">Thank you again for increasing my fascination with English! </span></p>\n\n<p dir=\"ltr\"><span dir=\"ltr\">Hope to see you again very soon! </span></p>\n\n<p dir=\"ltr\"><span dir=\"ltr\">Yours</span></p>\n\n<p dir=\"ltr\"><span dir=\"ltr\">Setare :) </span></p>\n', 'UOshs1477852278.jpg', '158', '1477852404', '1477852404', 'publish', '13', null);
