INSERT INTO `ym_news` VALUES ('58', 'نمره تافل آی بی تی خانم سارا علی نیا سال 2014', 'سلام آقای خادم،\nمعذرت میخواهم که تا امروز طول دادم تموم کردن رایتینگی که قرار بود بعد تافلم بفرستم براتون. در ضمن میخواستم یک دنیا تشکر کنم ازتون که رایتینگ من رو از اون حالتی که بود و در آزمون آزمایشی 21 شده بودم نجات دادین. همچنین ممنونم که بهم اعتماد به نفس دادین و \"اکوردینگلی\" من تونستم نمره ی خوبی بیارم. واقعا ممنونم ازتون واسه همه درس ها و نکته های طلایی ای که به ما یاد دادین. امیدوارم شما و خانواده تون همیشه خوشبخت و سربلند باشین.\nسارا علی نیا\n', '<p dir=\"rtl\">نمره یکی از دانشجویان تافل آی بی تی من، خانم سارا علی نیا، در سال 2014.</p>\n', 'ig1971478282857.jpg', '81', '1478282919', '1478282965', 'publish', '13', null);
