INSERT INTO `ym_news` VALUES ('111', 'نمره تافل آی بی تی آقای سینا رستگار سال 2016', 'سلام جناب خادم نمره من اومد و ١٠٧ شدم و رايتينگ ٢٦. درسته كه نمره خيلي بالايي نيست ولي در ١ماه وقت من واقعا خودم انتظارش رو نداشتم و اين نمره رو مديون شما هستم. خواستم از زحماتتون يكبار ديگه تشكر كنم گرچه به هيچ عنوان كافي نيست.', '<p>نمره تافل آی بی تی یکی از دانشجویانم، آقای سینا رستگار، سال 2016</p>\n', 'LYP4q1481032547.jpg', '166', '1481032721', '1481032721', 'publish', '8', null);
INSERT INTO `ym_news` VALUES ('112', 'نمره تافل آی بی تی و جی آر ای آقای مجتبی اسفندیاری سال 2016', 'جناب استاد خادم: با سلام و عرض ادب و احترام\nمن مجتبی اسفندیاری دانشجوی مکانیک شریف هستم که در ترم پاییز 94 در کلاس رایتینگ و اسپیکینگ شما شرکت کرده بودم. امروز تافل داشتم، از لیسنینگ های تکراری bird migration ,plant classification ,piano music اومده بود، دست شما درد نکنه خیلی خیلی ممنونم. \nاستاد نمره تافلم الان اومد. ریدینگ رو همیشه 28-29 میزدم ولی خب سر آزمون تمرکزم رو از دست دادم و کم شدم، رایتینگ رو هم که گفتم، سرعت تایپم کم بود و کانکلوژن ننوشتم وگرنه بیشتر می شدم، چون توی آزمون آزمایشی اسپیکینگ رو 22 شدم و اینجا 26، رایتینگ رو توی آزمایشی 24 شدم و اینجا هم 24، یعنی اگه همون رایتینگی که توی آزمون آزمایشی نوشتم اینحا هم مینوشتم راحت 27 - 28 میتونستم بگیرم ولیسنینگ رو اما همیشه در همین حد میزدم، آخرش یه راه درست برای لیسنینگ زدن نتونستم پیدا کنم ، اوایل نت برمیداشتم ، این اواخر و سر آزمون اصلا نت بر نداشتم، کامل متوجه میشدم ولی خب یادم میرفت و موضوع از دستم در می رفت\nریدینگ 25 \nلیسنینگ 23 \nاسپیکینگ 26 \nرایتینگ 24\nاگه ریدینگ و رایتینگ رو مثل همیشه میدادم میتونستم بالای 100 بشم اما نشد، بازم خدا رو شکر، خیلی خیلی از شما ممنونم. اصلا در کل معمولا مجموع نمره لیسنینگ و ریدینگ بچه ها از مجموع نمره اسپیکینگ و رایتینگ شون بیشتر میشه، ولی برای من برعکس شد، ای کاش اون دو تا رو هم شما کلاس میذاشتین و من میومدم پیش شما، چون همون ترم من کلاس ریدینگ و لیسنینگ رو توی شریف یکی دو جلسه رفتم و دیگه نرفتم. اینم بگم که معمولا توی رایتینگ هام همیشه حدود 20 تا غلط تایپی داشتم، چون سرم پایین بود و خیلی به مانیتور نگاه نمیکنم، مثلا بارها شده  and رو نوشتمadn  احتمالا اینا هم نمره کم کرده.\nمن هیچ کلاس جی آر ای نرفتم ، فرصت هم نشد واسش بخونم ، به خصوص رایتینگ حتی یه دونه رایتینگ هم قبل آزمون ننوشتم و فقط با مطالبی که از کلاس رایتینگ شما یاد گرفتم تونستم رایتینگ جی آر ای رو 3.5 بگیرم.\n', '<p>نمره تافل آی بی تی و جی آر ای یکی از دانشجویان من، آقای مجتبی اسفندیاری، سال 2016</p>\n', 'MRotp1481033627.jpg', '263', '1481033807', '1481033807', 'publish', '8', null);
INSERT INTO `ym_news` VALUES ('113', 'تصحیح رایگان  نمونه رایتینگ های جی آر ای', 'خبر خوبی دارم برای دوستانی که قصد شرکت در آزمون GRE دارند و مایلند رایتینگ خود را محک بزنند.\n\n', '<p>با سلام خدمت دوستان عزیز. <br />\nخبر خوبی دارم برای دوستانی که قصد شرکت در آزمون GRE دارند و مایلند رایتینگ خود را محک بزنند.</p>\n\n<p>این دوستان می توانند با ارائه تصویری از ثبت نام خود، یک نمونه رایتینگ به انتخاب خود (Issue یا Argument) را برای موسسه ارسال نمایند تا بصورت رایگان برایشان تصحیح گردد. با این کار دوستان می توانند نمره‌ رایتینگ خود را بر اساس معیار ETS داشته باشند و فيدبک جامعی در مورد محتوا، انسجام، لغت و گرامر رایتینگ خود بصورت مکتوب دریافت نمایند.<br />\n <br />\nاگر این دوستان راضی باشند و موافقت نمایند، محتوای نوشتاری این رایتینگ ها در متون و بحث های آموزشی موسسه مورد استفاده قرار خواهد گرفت.</p>\n\n<p>لازم به ذکر است که اطلاعات فردی دوستان نزد آموزشگاه محرمانه خواهد ماند. </p>\n\n<p>دوستانی که مایل هستند می‌توانند تصویر ثبت نام خود را به ایمیل آموزشگاه (Pardis@avayeshahir.com) ارسال نمایند.</p>\n\n<p>با تشکر امیر خادم.</p>\n', 'TX5FB1484133744.jpg', '206', '1481139981', '1484153653', 'publish', '4', null);
INSERT INTO `ym_news` VALUES ('114', ' گرامر مقدماتی برای تافل، آیلتس، جی آر ای، و جی مت', '', '<p>دوره گرامر مقدماتی برای آزمون های تافل، آیلتس، جی آر ای، جی مت، و تافل دکتری<br />\nدر کلاس های گرامر مقدماتی برگزار انواع جملات، زمانها، جملات معلوم و مجهول، صفت، قید، اسم، فعل، ضمایر، و افعال کمکی تدریس خواهد شد. لازم به ذکر است که بخشی از این مطالب از \"جزوه گرامر مقدماتیِ\" اینجانب، امیر خادم، و بخش هایی هم از جزوه استاد ایزدیفر تدریس می گردد.</p>\n', 'bgLiR1481620117.jpg', '202', '1481621911', '1481621911', 'publish', '5', null);
INSERT INTO `ym_news` VALUES ('115', 'اسپیکینگ تافل آی بی تی گروه 1', 'این دوره روزهای 4 شنبه 5 تا 8 برگزار می شود و از تاریخ 29 دی ماه 1395 شروع و تا 4 اسفند 1395 ادامه خواهد داشت. ', '<p dir=\"rtl\" style=\"text-align:justify;\">دردوره اسپیکینگ تافل آی بی تی، علاوه بر معرفی هر 6 صورت سوال این بخش و تکنیک های مورد نیاز، تلاش خواهد شد که دانش زبانی داوطلبین هم بهتر شود. در ضمن <u><strong>مهارت نت برداری</strong></u> که یکی از اصلی ترین مهارت های تافل آی بی تی است نیز با دقت و ظرافت تدریس خواهد شد. با برگزاری این دوره ها، امیدواریم که بتوانیم سهم کوچکی در بالا بردن سطح علمی دانشجویان عزیزمان داشته باشیم و راه را برای پیشرفت های آتی آنها هموار سازیم.</p>\n\n<p dir=\"rtl\" style=\"text-align:justify;\">با آرزوی بهروزی همه عزیزان</p>\n\n<p dir=\"rtl\">مدرس تافل موسسه پردیس آوای شهیر</p>\n\n<p>امیر خادم المله</p>\n', 'UKRad1481650606.jpg', '249', '1481650779', '1481650779', 'publish', '5', null);
INSERT INTO `ym_news` VALUES ('116', 'تخفیف سایت پرشین دانشجو جهت امور مربوط به اپلای', 'سایت پرشین دانشجو به گروه زبان آقای خادم (ٱوای شهیر)  لطف داشته و کد تخفیفی برای صدور مستر کارت مجازی ویژه  Apply (ثبت نام آزمون تافل، GRE، اپلیکیشن فی، وقت سفارت و غیره) اختصاص داده اند. این کارت دو ساعته صادر میشود و پنل آنلاین بانکی هم دارد. هزینه صدور کارت 5 دلار بوده و با کد تخفیف PD_KHADEM_2017 کارمزد شما 4 درصد محسابه میشود.\n\nپس از وارد کردن کد تخفیف PD_KHADEM_2017 این عبارت ظاهر میشود \"کد تخفیف صحیح است (ویژه کلاس زبان استاد خادم)\".\n', '<p>سایت پرشین دانشجو به گروه زبان آقای خادم (ٱوای شهیر)  لطف داشته و کد تخفیفی برای صدور مستر کارت مجازی ویژه  Apply (ثبت نام آزمون تافل، GRE، اپلیکیشن فی، وقت سفارت و غیره) اختصاص داده اند. این کارت دو ساعته صادر میشود و پنل آنلاین بانکی هم دارد. هزینه صدور کارت 5 دلار بوده و با کد تخفیف PD_KHADEM_2017 کارمزد شما 4 درصد محسابه میشود.</p>\n\n<p><a href=\"http://persiandaneshjoo.ir/NEWWEBSITE/?m=order&amp;u=1&amp;o=12&amp;t=%D9%85%D8%B3%D8%AA%D8%B1-%DA%A9%D8%A7%D8%B1%D8%AA-%D9%85%D8%AC%D8%A7%D8%B2%DB%8C-ezGyft-(%D8%BA%DB%8C%D8%B1%D9%82%D8%A7%D8%A8%D9%84-%D8%B4%D8%A7%D8%B1%DA%98)&amp;id=24\">http://persiandaneshjoo.ir/NEWWEBSITE/?m=order&amp;u=1&amp;o=12&amp;t=%D9%85%D8%B3%D8%AA%D8%B1-%DA%A9%D8%A7%D8%B1%D8%AA-%D9%85%D8%AC%D8%A7%D8%B2%DB%8C-ezGyft-(%D8%BA%DB%8C%D8%B1%D9%82%D8%A7%D8%A8%D9%84-%D8%B4%D8%A7%D8%B1%DA%98)&amp;id=24</a></p>\n', 'qfyvv1481987553.jpg', '219', '1481826085', '1481989645', 'publish', '4', null);
