INSERT INTO `ym_news` VALUES ('118', 'رایتینگ تافل آی بی تی گروه 2', 'سلام به همه عزیزان. از اونجایی که برای کلاس رایتینگ ۵ شنبه بیش از ظرفیت ثبت نام شده، یک کلاس رایتینگ دیگه برای روز ۲شنبه ۵ تا هشت تعریف میشه. اگه دوستان لطف کنن اونجا ثبت نام کنن ممنون میشم. امیر خادم.', '<p>تاریخ شروع دوره رایتینگ 27 دی 1395 است و تاریخ پایان دوره 23 اسفند. البته مجموعا 9 جلسه کلاس در روز دوشنبه برگزار شده و یک جلسه جبرانی هم بعدا در نظر گرفته می شود.</p>\n', 'qmnjF1484129968.jpg', '120', '1484130463', '1484694146', 'publish', '5', null);
