INSERT INTO `ym_news` VALUES ('120', 'کامل ترین نرم افزار فرمول های جی آر ای', 'کامل ترین نرم افزار فرمول های جی آر ای\nاین نرم افزار نسخه جدید و کامل تر نرم افزار قبلی است که تمام اصطلاحات کوانت جی آر ای را همراه با تلفظ و معنی فارسی و شکل ارائه میدهد.', '<p>این نرم افزار نسخه جدید و کامل تر نرم افزار قبلی است که تمام اصطلاحات کوانت جی آر ای را همراه با تلفظ و معنی فارسی و شکل ارائه میدهد.</p>\n\n<p>https://drive.google.com/open?id=0B8V9PkAm4QfTVnZ6M3JqY0dEQnc</p>\n', '3Cepq1484394775.jpg', '105', '1484395553', '1484732616', 'publish', '15', null);
INSERT INTO `ym_news` VALUES ('121', 'به روز رسانی جزوات و برنامه درسی تافل ', 'به روز رسانی تمامی جزوات گرامر مقدماتی و پیشرفته و ارائه یکی از کامل ترین برنامه های درسی تافل با جزییات کامل. ', '<p>تمامی جزوات گرامر مقدماتی و پیشرفته به روز رسانی شدند  و یکی از کامل ترین برنامه های درسی تافل با جزییات کامل خدمت عزیزان ارائه گردید. </p>\n\n<p>https://drive.google.com/open?id=0B8V9PkAm4QfTazQ5aFZjSTZLQkU</p>\n\n<p>https://drive.google.com/open?id=0B8V9PkAm4QfTd2FZaVRyVEFfM0U</p>\n\n<p>https://drive.google.com/open?id=0B8V9PkAm4QfTT2J0ZGdQMHJoeFU</p>\n', '3UN7H1484690388.jpg', '108', '1484690998', '1484732579', 'publish', '15', null);
