INSERT INTO `ym_translations` VALUES ('4330', '{{class_category_file_links}}', '403', 'title', 'en', 'COBUILD Key Words for IELTS: Book 3 Advanced PDF');
INSERT INTO `ym_translations` VALUES ('4331', '{{class_category_file_links}}', '403', 'summary', 'en', 'The Collins COBUILD Key Words for IELTS series is a brand-new range of three graded books which contain the essential vocabulary students need to succeed in the IELTS exam. These books have been specially created for foreign learners of English who plan to take the IELTS exam to demonstrate that they have the required ability to communicate effectively in English, either at work or at university. Book 3 Advanced covers the harder vocabulary that may appear in the exam, and that students may need to master to achieve the scores required by the top universities and employers.');
INSERT INTO `ym_translations` VALUES ('4332', '{{class_category_file_links}}', '404', 'title', 'en', 'Cambridge IELTS 1');
INSERT INTO `ym_translations` VALUES ('4333', '{{class_category_file_links}}', '404', 'summary', 'en', 'Cambridge Practice Tests for IELTS 1 is written by experienced IELTS examiners, one based in Britain and one based in Australia. The Student\'s Book contains an introduction to the different modules of the exam together with an explanation of the different IELTS question types and how to approach them. The inclusion of annotated keys and tapescripts for each test makes the book ideal for students working partly or entirely on their own. The Audio CDs contain listening material carefully chosen to reflect the reality of the exam in terms of timing, format and the types of speaker and accent used.');
INSERT INTO `ym_translations` VALUES ('4334', '{{class_category_file_links}}', '405', 'title', 'en', 'Cambridge IELTS 2');
INSERT INTO `ym_translations` VALUES ('4335', '{{class_category_file_links}}', '405', 'summary', 'en', 'This collection of practice material for the International English Language Testing system (IELTS) has been specially prepared for publication by the University of Cambridge Local Examinations Syndicate. It provides students with an excellent opportunity to familiarise themselves with IELTS and to practise examination techniques using authentic test material. This book includes an introduction to these different modules together with an explanation of the scoring system used by Cambridge ESOL. The inclusion of a comprehensive section of answers and tapescripts means that the material is ideal for students working partly or entirely on their own.');
INSERT INTO `ym_translations` VALUES ('4336', '{{class_category_file_links}}', '406', 'title', 'en', 'Cambridge IELTS 3');
INSERT INTO `ym_translations` VALUES ('4337', '{{class_category_file_links}}', '406', 'summary', 'en', 'Cambridge IELTS 3 provides students with an excellent opportunity to familiarise themselves with IELTS and practise examination techniques using authentic test material. This collection includes practice in the updated Speaking test (June 2001 syllabus), plus extra Reading and Writing modules for General Training candidates. The book includes an introduction to these different modules together with an explanation of the scoring system used by Cambridge ESOL. The comprehensive section of answers and tapescripts means that the material is ideal for students working partly or entirely on their own. A self-study pack (Student\'s Book with answers and Audio CD) is also available.');
