INSERT INTO `ym_translations` VALUES ('4356', '{{class_category_file_links}}', '416', 'title', 'en', 'The Official Cambridge Guide to IELTS ');
INSERT INTO `ym_translations` VALUES ('4357', '{{class_category_file_links}}', '416', 'summary', 'en', 'The Official Cambridge Guide to IELTS is THE definitive guide to IELTS. It contains all you need to succeed in the exam. Skill-building exercises cover all of the question types in the exam for both the General Training and Academic Modules. Our research into real IELTS candidates\' exam answers - and the mistakes they make, helps you avoid the same ones! Eight official practice tests and a focus on test-taking strategy help you maximise your score. Videos of the Speaking test, and all the listening material, including the tests, can be found on the DVD-ROM or downloaded via an accompanying App from iTunes or Google Play. IELTS is jointly managed by the British Council, IDP: IELTS Australia and Cambridge English Language Assessment.');
