INSERT INTO `ym_translations` VALUES ('4369', '{{class_category_file_links}}', '420', 'title', 'en', 'New Insight into IELTS PDF + CD');
INSERT INTO `ym_translations` VALUES ('4370', '{{class_category_file_links}}', '420', 'summary', 'en', 'New Insight into IELTS offers comprehensive preparation and practice for IELTS. By exploring the test paper by paper, and looking in detail at each task type, the course gradually builds up the skills, language and test techniques students need to approach IELTS with confidence. The course contains a detailed introduction to the test and a full answer key and is equally suitable for use in the classroom or for self-study. The material is intended for use with students whose current level is around Band 6 and is suitable for both Academic and General Training candidates.');
INSERT INTO `ym_translations` VALUES ('4371', '{{class_category_file_links}}', '421', 'title', 'en', 'IELTS Preparation and Practice - General Module - Reading and Writing + CD');
INSERT INTO `ym_translations` VALUES ('4372', '{{class_category_file_links}}', '421', 'summary', 'en', 'Your favourite IELTS resource is back ...better than ever!The IELTS Preparation and Practice series is designed to meet the needs of students preparing to take the IELTS (International English Language Testing System) test.Each book in this series reflects the format of the IELTS test and offers a complete guide to developing the required skills for Listening and Speaking, Reading and Writing.The series provides comprehensive preparation for and practice in the complete range of skills tested in the IELTS. The focus is on both analysing the process involved in doing the exam questions and competing practice activities.The materials in the IELTS Preparation and Practice series can be used in the classroom or for individual study.');
