INSERT INTO `ym_translations` VALUES ('4374', '{{class_category_file_links}}', '422', 'summary', 'en', 'Part of the highly-successful Oxford Word Skills series, it helps students improve their vocabulary and vocabulary learning skills by presenting and practising over 1000 Idioms and Phrasal Verbs in everyday contexts. Lots of opportunity for personalization and review gives them the confidence to use their new vocabulary.');
